SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;
SET time_zone = "+00:00";
create database if not exists e_commerce;
create table admin_info(admin_id int(10) NOT NULL AUTO_INCREMENT,admin_name varchar(100) NOT NULL,PRIMARY KEY (admin_id),
admin_email varchar(100) NOT NULL, admin_password varchar(100) NOT NULL)ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=latin1;
create table user_info(
userid int(10) NOT NULL AUTO_INCREMENT,PRIMARY KEY (userid),
first_name varchar(100) NOT NULL, 
last_name varchar(100) NOT NULL,
age int(10) not null,
gender varchar(10) not null,
email varchar(100) NOT NULL, 
passwrd varchar(200) NOT NULL,
mobile int(10) not null,
address varchar(100) not null,
city varchar(100) not null,
state1 varchar(100) not null,
zip int(10) not null)ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;
create table categories(cat_id int(100) NOT NULL auto_increment,PRIMARY KEY(cat_id),
cat_title TEXT NOT NULL)ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=latin1;
create table sub_categories(sub_cat_id int(100) NOT NULL auto_increment,PRIMARY KEY (sub_cat_id),cat_id int(100),
foreign key (cat_id) references categories(cat_id), sub_cat_title text not null)ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=latin1;
CREATE TABLE products(
  product_id int(100) NOT NULL auto_increment,PRIMARY KEY (product_id),
  cat_id int(100),
  sub_cat_id int(100),
  foreign key(cat_id) references categories(cat_id),
  foreign key(sub_cat_id) references sub_categories(sub_cat_id) ,
  product_title varchar(255) NOT NULL,
  product_price int(100) NOT NULL,
  product_desc text NOT NULL,
  product_image varchar(255) NOT NULL)ENGINE=InnoDB AUTO_INCREMENT=2051 DEFAULT CHARSET=latin1;
CREATE TABLE cart(
  id int(10) NOT NULL auto_increment, PRIMARY KEY (id), 
  product_id int(10) default null,
  userid int(10) default null,
  qty int(10) NOT NULL,
  product_desc varchar(255) NOT NULL,
  price int(10) NOT NULL) ENGINE=InnoDB  DEFAULT CHARSET=latin1;
CREATE TABLE orders(
  order_id int(10) NOT NULL auto_increment,PRIMARY KEY(order_id),
  userid int(100) default null,
  f_name varchar(255) default NULL,
  email varchar(255) default NULL,
  address varchar(255) default NULL,
  city varchar(255) default NULL,
  state1 varchar(255) default NULL,
  zip int(10) default NULL,
  product_id int(10) default null,
  cardname varchar(255) default NULL,
  cardnumber varchar(20) default NULL,
  expdate varchar(255) default NULL,
  cvv int(5) default NULL)ENGINE=InnoDB AUTO_INCREMENT=111 DEFAULT CHARSET=latin1;

  



create table trash(
  cat_id int(100) NOT NULL,
  sub_cat_id int(100) NOT NULL,
  product_id int(100) NOT NULL,
  product_title varchar(255) NOT NULL,
  product_price int(100) NOT NULL,
  product_desc text NOT NULL,
  product_image varchar(500) NOT NULL
)ENGINE=InnoDB DEFAULT CHARSET=latin1;

COMMIT;


